/* eslint-disable import/no-extraneous-dependencies */

// https://developer.chrome.com/docs/extensions/mv3/manifest/
// https://developer.chrome.com/docs/extensions/reference/

import { gitRef } from 'git-ref';
import pkg from './package.json' assert { type: 'json' };

export const makeManifest = (): chrome.runtime.ManifestV3 => ({
  manifest_version: 3,
  name: 'New Tab',
  description: pkg.description,
  version: pkg.version,
  // shippable releases should not have a named version
  version_name: process.env.CI ? undefined : gitRef().replace(/^v/, ''),
  minimum_chrome_version: '113',
  homepage_url: pkg.homepage,
  icons: {
    16: 'icon16.png',
    48: 'icon48.png',
    128: 'icon128.png',
  },
  permissions: [
    'bookmarks',
    'favicon',
    'history',
    'sessions',
    'storage',
    'tabs',
    'topSites',
  ],
  chrome_url_overrides: {
    newtab: 'newtab.html',
  },
  background: {
    service_worker: 'sw.js',
  },
  options_ui: {
    page: 'settings.html',
    // open_in_tab: true, // dev only
  },
  offline_enabled: true,
  incognito: 'not_allowed',
  content_security_policy: {
    extension_pages: [
      "default-src 'none'",
      "script-src 'self'",
      "style-src 'self'",
      "img-src 'self'",
      "base-uri 'none'",
    ].join(';'),
  },
  // https://chrome.google.com/webstore/detail/new-tab/cpcibnbdmpmcmnkhoiilpnlaepkepknb
  key: '',
});
